import styles from "./Playerpage.module.css";
import React, {useEffect, useState} from "react";
import Footer from "../../../../../../../dbs-kbolog/src/main/frontend/src/components/_Layout/Footer";
import HeaderImg from "../../../../../../../dbs-kbolog/src/main/frontend/src/image/playerHeader.jpg";
import PlayerTable from "../../../../../../../dbs-kbolog/src/main/frontend/src/components/_Table/PlayerTable";
import Calendar from "react-calendar";
import {useNavigate} from "react-router-dom";

export default function Playerpage() {
    const [player, setPlayer] = useState([]);
    const navigate = useNavigate();
    useEffect(() => {
        fetch("/api/player/cheer")
            .then(res=>res.json())
            .then(data=> {
                setPlayer(data);
            })
            .catch((err) => {
                console.error("API fetch error:", err); // ÏóêÎü¨ Ï≤òÎ¶¨
            });
    }, []);


    return (
        <div className={styles.default}>
            <div className={styles.cropping}>
                <img className={styles.headerImg} src={HeaderImg} alt="Header" />
            </div>
            <div className={styles.inner}>
                <div className={styles.title}>Í¥ÄÏã¨ ÏÑ†Ïàò
                    <input
                        type="button"
                        value="üíïÌå¨ Îû≠ÌÇπ"
                        className={styles.rankBtn}
                        onClick={() => navigate('/players/rank')}
                    />
                </div>
                <br/>
                <PlayerTable pId={0}/>

                {player.map(data => {
                    // cheerDateÎ•º Date Í∞ùÏ≤¥Î°ú Î≥ÄÌôò
                    const cheerDate = new Date(data.cheerDate);
                    const today = new Date();

                    // Î∞ÄÎ¶¨Ï¥à Îã®ÏúÑÎ°ú Ï∞®Ïù¥ Í≥ÑÏÇ∞ ÌõÑ, Ïùº Îã®ÏúÑÎ°ú Î≥ÄÌôò
                    const diffInMs = today - cheerDate; // Î∞ÄÎ¶¨Ï¥à Ï∞®Ïù¥
                    const diffInDays = Math.floor(diffInMs / (1000 * 60 * 60 * 24)); // Ïùº Îã®ÏúÑ

                    return (
                        <PlayerTable
                            pId={data.playerId}
                            pName={data.playerName}
                            pNum={data.playerNumber}
                            pPosition={data.playerPosition}
                            pTeam={data.teamName}
                            pSponsor={data.teamSponsor}
                            cheerDate={`${diffInDays}Ïùº`}
                        />
                    );
                })
                }
                <PlayerTable pId={-1}/>

            </div>
            <Footer/>
        </div>
    );
}
