import React from 'react';
import { useState } from "react";
import styled from 'styled-components';
import FormField from "../../components/_Form/FormField";
import InfoItem from "../../components/_Form/InfoItem";
import axios from 'axios';
import { useNavigate } from "react-router-dom";
import DividendResult from "../result/DividendResult";


const DividendCalc = () => {
    const navigate = useNavigate();
    const [options, setOptions] = useState({
        tax: "Í≥ºÏÑ∏",
        inflation: "3.0%",
        reinvest: "No",
        dividendGrowth: "8.0%",
        initialInvestment: "0",
        dividendYield: "3.6%",
        monthlyInvestment: "0",
        dividendCycle: "Î∂ÑÍ∏∞",
        monthlyIncrease: "0"
    });

    const optionFields = [
        { label: "Í≥ºÏÑ∏Ïó¨Î∂Ä", name: "tax", options: ["Í≥ºÏÑ∏", "ÎπÑÍ≥ºÏÑ∏"] },
        { label: "üìä Î¨ºÍ∞ÄÏÉÅÏäπÎ•† (Ïù∏ÌîåÎ†àÏù¥ÏÖò)", name: "inflation", options: ["2.0%", "2.5%", "3.0%", "3.5%", "4.0%"] },
        { label: "Î∞∞ÎãπÏû¨Ìà¨Ïûê", name: "reinvest", options: ["Yes", "No"] },
        { label: "üìà Î∞∞ÎãπÏÑ±Ïû•Î•†\t", name: "dividendGrowth", options: ["6.0%", "7.0%", "8.0%", "9.0%", "10.0%"] },
        { label: "Ï¥àÍ∏∞ Ìà¨ÏûêÍ∏à", name: "initialInvestment", type: "number" },
        { label: "üí∏ ÏãúÍ∞ÄÎ∞∞ÎãπÎ•†\t", name: "dividendYield", options: ["3.0%", "3.2%", "3.4%", "3.6%", "4.0%"] },
        { label: "Ïõî Ï†ÅÎ¶ΩÏãù Ìà¨ÏûêÍ∏à", name: "monthlyInvestment", type: "number" },
        { label: "üìÜ Î∞∞ÎãπÏ£ºÍ∏∞", name: "dividendCycle", options: ["Ïõî", "Î∂ÑÍ∏∞"] },
        { label: "Ïõî Ï†ÅÎ¶ΩÏãù+Ï¶ùÏï°", name: "monthlyIncrease", type: "number" }
    ];

    const infoList1 = [
        { term: "Í≥ºÏÑ∏Ïó¨Î∂Ä", description: "ÎØ∏Íµ≠ ÏßÅÏ†ëÌà¨ÏûêÎäî Í≥ºÏÑ∏(ÏßÅÌà¨)Î•º ÏÑ†ÌÉù. ÏÑ∏Ï†Ñ Î∞∞ÎãπÍ∏àÏóêÏÑú 15% Í≥µÏ†ú.", note: "* ÎπÑÍ≥ºÏÑ∏ Ìà¨ÏûêÏùò Í≤ΩÏö∞, Ïù∏Îãπ Ìà¨ÏûêÌïúÎèÑ Ïú†Ïùò." },
        { term: "Î∞∞ÎãπÏû¨Ìà¨Ïûê", description: "Î∞∞ÎãπÏû¨Ìà¨Ïûê Ïó¨Î∂Ä ÏÑ†ÌÉù." },
        { term: "Ï¥àÍ∏∞ Ìà¨ÏûêÍ∏à", description: "0Í∞úÏõî Ï∞® Ï¥àÍ∏∞ Ìà¨ÏûêÍ∏à. (0Ïõê~)" },
        { term: "Ïõî Ï†ÅÎ¶ΩÏãù Ìà¨ÏûêÍ∏à", description: "Îß§Ïõî Ï†ÅÎ¶ΩÏãùÏúºÎ°ú Ìà¨ÏûêÌï† Í∏àÏï°. (0Ïõê~)" },
        { term: "Ïõî Ï†ÅÎ¶ΩÏãù+Ï¶ùÏï°", description: "Îß§ÎÖÑ Ïõî Ï†ÅÎ¶ΩÏãù Ìà¨ÏûêÍ∏àÏóêÏÑú ÎäòÎ†§ÎÇòÍ∞à Í∏àÏï°.", note: "(Ïòà: Îß§ÎÖÑ 10ÎßåÏõêÏî© Ï¶ùÍ∞ÄÌïòÎ©¥ 100,000 ÏûÖÎ†•)" }
    ];

    const infoList2 = [
        { term: "üìä Î¨ºÍ∞ÄÏÉÅÏäπÎ•†(=ÏÜåÎπÑÏûêÎ¨ºÍ∞ÄÏßÄÏàò)", description: "ÏµúÍ∑º 10ÎÖÑ ÌèâÍ∑† 2.4% (ÏÜåÎπÑÏûêÎ¨ºÍ∞ÄÏßÄÏàò). Î≥¥ÏàòÏ†ÅÏúºÎ°ú 3~4% ÏÑ§Ï†ï Í∂åÏû•.", link: "https://cafe.naver.com/divclub/834" },
        { term: "üìà Î∞∞ÎãπÏÑ±Ïû•Î•†(=YoYÎ∞∞ÎãπÏÑ±Ïû•)", description: "SCHD Í∏∞Ï§Ä 6~12% (JEPI Îì± Ïª§Î≤ÑÎìúÏΩúÏùÄ 0~1%)", link: "https://www.youtube.com/watch?v=POpZjewlc80" },
        { term: "üí∏ ÏãúÍ∞ÄÎ∞∞ÎãπÎ•†(=Î∞∞ÎãπÍ∏à/Ï£ºÍ∞Ä)", description: "SCHD Í∏∞Ï§Ä 3% ÎÇ¥Ïô∏ (JEPI 6~10%). ÏãúÍ∞ÑÏù¥ ÏßÄÎÇòÎèÑ ÏùºÏ†ïÌïú ÏàòÏ§Ä Ïú†ÏßÄ.", note: "* Ìà¨ÏûêÏõêÍ∏à ÎåÄÎπÑ Î∞∞ÎãπÏàòÏùµÎ•†Í≥ºÎäî Îã§Î¶Ñ." },
        { term: "üìÜ Î∞∞ÎãπÏ£ºÍ∏∞", description: "SCHD Î∂ÑÍ∏∞Î∞∞Îãπ / JEPI ÏõîÎ∞∞Îãπ. ÏÉÅÌíàÏóê Îî∞Îùº ÏÑ§Ï†ï ÌïÑÏöî." }
    ];

    const [result, setResult] = useState(null);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setOptions({ ...options, [name]: value });
    };


    const handleSubmit = async (e) => {
        e.preventDefault(); // Ìèº Ï†úÏ∂ú Î∞©ÏßÄ

        const payload = {
            tax: options.tax,
            inflation: parseFloat(options.inflation),
            reinvest: options.reinvest === "Yes",
            dividendGrowth: parseFloat(options.dividendGrowth),
            initialInvestment: parseFloat(options.initialInvestment),
            dividendYield: parseFloat(options.dividendYield),
            monthlyInvestment: parseFloat(options.monthlyInvestment),
            dividendCycle: options.dividendCycle,
            monthlyIncrease: parseFloat(options.monthlyIncrease)
        };

        try {
            const res = await axios.post("http://localhost:8080/api/dividend", payload, {
                headers: {
                    "Content-Type": "application/json",
                    "Accept": "application/json"
                }
            });
            console.log("Payload:", payload);
            console.log("Response:", res.data);
            setResult(res.data)
            // ÏÑ±Í≥µ ÌõÑ Ï≤òÎ¶¨Ìï† Î°úÏßÅÏù¥ ÏûàÏúºÎ©¥ Ïó¨Í∏∞Ïóê
        } catch (err) {
            console.error("Axios POST error:", err);
            if (err.response) {
                console.error("Status:", err.response.status);
                console.error("Data:", err.response.data);
            }
        }
    };



    return (
        <PostContainer>
            <PostTitle>Î∞∞ÎãπÍ∏à Ïû¨Ìà¨Ïûê Í≥ÑÏÇ∞Í∏∞</PostTitle>
            <PostContent>
                <OptionsGrid>
                    {optionFields.map((field, index) => (
                        <FormField
                            key={index}
                            label={field.label}
                            name={field.name}
                            value={options[field.name]}
                            onChange={handleChange}
                            options={field.options}
                            type={field.type}
                        />
                    ))}
                    <ButtonWrap>
                        <Button onClick={handleSubmit}>Í≥ÑÏÇ∞ÌïòÍ∏∞</Button>
                    </ButtonWrap>
                </OptionsGrid>
                <ContentWrapper>
                    {result ? (
                        <DividendResult result={result} />
                    ) :
                    (
                        <>
                            <Section>
                                <InfoItem items={infoList1} />
                            </Section>
                            <Section>
                                <InfoItem items={infoList2} />
                            </Section>
                        </>
                    )}
                </ContentWrapper>

            </PostContent>
        </PostContainer>
    );
};
const DefinitionList = styled.dl`
  display: flex;
  flex-direction: column;
  gap: 15px;
`;
const PostContainer = styled.article`
    max-width: 800px;
    margin: 0 auto;
    padding-left: 20px;
`;

const PostTitle = styled.h2`
    font-size: 2.5rem;
    color: #333;
    font-family: "Hanna", sans-serif;
`;


const PostContent = styled.div`
    font-size: 1.1rem;
    line-height: 1.6;
    color: #444;
`;

const ContentWrapper = styled.div`
  display: flex;
  justify-content: space-between; /* ÏÑπÏÖò ÏÇ¨Ïù¥ Ïó¨Î∞± Í∑†Îì± */
  gap: 20px; /* ÏÑπÏÖò Í∞ÑÍ≤© */
  margin-top: 20px;

  @media screen and (max-width: 768px) {
    flex-direction: column; /* ÌôîÎ©¥Ïù¥ ÏûëÏúºÎ©¥ ÏÑ∏Î°úÎ°ú Î∞∞Ïπò */
  }
`;

const Section = styled.section`
  flex: 1; /* Îëê ÏÑπÏÖòÏù¥ Í∞ôÏùÄ ÎπÑÏú®Î°ú Í≥µÍ∞Ñ Ï∞®ÏßÄ */
  padding: 0 15px;
  background: #f9f9f9;
  border-radius: 8px;
  min-width: 300px; /* ÎÑàÎ¨¥ Ï¢ÅÏïÑÏßÄÏßÄ ÏïäÎèÑÎ°ù */
`;



const ButtonWrap = styled.div`
    text-align: right;
`

const Button = styled.button`
    background: #007bff;
    color: white;
    font-weight: bold;
    padding: 10px 19px;
    border: none;
