// components/DividendResult.js

import React from 'react';
import styled from 'styled-components';

const DividendResult = ({ result }) => {
    if (!result) return null;

    return (
        <ResultWrapper>
            <h3>üìÖ Ïõî/Î∂ÑÍ∏∞Î≥Ñ Î∞∞Îãπ ÎÇ¥Ïó≠</h3>
            <TableScrollWrapper>
                <TableWrapper>
                    <thead>
                    <tr>
                        <th>Ïó∞ÎèÑ</th>
                        <th>Ïõî or Î∂ÑÍ∏∞</th>
                        <th>Î∞∞ÎãπÍ∏à</th>
                    </tr>
                    </thead>
                    <tbody>
                    {result.monthlyResults.map((item, index) => (
                        <tr key={index}>
                            <td>{item.year}</td>
                            <td>{item.month}</td>
                            {/* or Î∂ÑÍ∏∞ ÌëúÏãú */}
                            <td>{item.dividend.toLocaleString()}</td>
                        </tr>
                    ))}
                    </tbody>
                </TableWrapper>
            </TableScrollWrapper>

            <Summary>
                <p>ÏµúÏ¢Ö Ï¥ù Ìà¨ÏûêÍ∏à : {result.totalInvestment.toLocaleString()} Ïõê</p>
                <p>ÎàÑÏ†Å Î∞∞ÎãπÍ∏à : {result.totalDividends.toLocaleString()} Ïõê</p>
            </Summary>
        </ResultWrapper>
    );
};


// Ïä§ÌÉÄÏùº Ïª¥Ìè¨ÎÑåÌä∏ (ÏõêÎûò ÏûàÎçò Í≤É Ïì∞Î©¥ Îèº)
const ResultWrapper = styled.div`
    padding: 0px 20px;
    border-radius: 12px;
    background: #f9f9f9;
    //width : 100%;
`;

const TableScrollWrapper = styled.div`
    width: 100%;
    overflow-x: auto;
`;

const TableWrapper = styled.table`
    border-collapse: collapse;
    font-size: 1rem;
    min-width: 660px; /* Ìëú ÏµúÏÜå ÎÑàÎπÑ */
    width: 100%;

    th, td {
        border: 1px solid #ddd;
        padding: 8px 12px;
        text-align: left;
        white-space: nowrap;
        max-width: 10px;
        overflow: hidden;
        text-overflow: ellipsis;
        font-size: 0.7rem;
    }

    th {
        background-color: #f0f8ff;
        color: #333;
        font-weight: 600;
    }

    tr:nth-child(even) {
        background-color: #fafafa;
    }

    tr:hover {
        background-color: #f1f1f1;
    }
`;



const Summary = styled.div`
    margin-top: 20px;
    font-size: 0.9rem;
    font-weight: bold;
`;


export default DividendResult;
