const InfoItem = ({ items }) => (
    <DefinitionList>
        {items.map(({ term, description, note, link }, index) => (
            <DefinitionItem key={index}>
                <Term>{term}</Term>
                <Description>{description}</Description>
                {note && <Note>{note}</Note>}
                {link && <Link href={link}>참고 링크</Link>}
            </DefinitionItem>
        ))}
    </DefinitionList>
);

export default InfoItem;
