// components/DividendResult.js

import React from 'react';
import styled from 'styled-components';

const DividendResult = ({ result }) => {
    if (!result) return null;

    return (
        <ResultWrapper>
            <h3>📊 배당 계산 결과</h3>
            <table>
                <thead>
                <tr>
                    <th>연차</th>
                    <th>투자원금</th>
                    <th>연간 배당금</th>
                    <th>배당률 (%)</th>
                    <th>주가 수익</th>
                    <th>연간 추가</th>
                    <th>총 평가액</th>
                    <th>누적 배당</th>
                </tr>
                </thead>
                <tbody>
                {result.yearlyResults.map((year, index) => (
                    <tr key={index}>
                        <td>{year.year}</td>
                        <td>{year.investedAmount.toLocaleString()}</td>
                        <td>{year.annualDividend.toLocaleString()}</td>
                        <td>{Number(year.dividendRate).toFixed(2)}</td>
                        <td>{year.priceGrowth.toLocaleString()}</td>
                        <td>{year.yearlyAddition.toLocaleString()}</td>
                        <td>{year.totalValue.toLocaleString()}</td>
                        <td>{year.accumulatedDividend.toLocaleString()}</td>
                    </tr>
                ))}
                </tbody>
            </table>

            <Summary>
                <p><strong>최종 총 투자금:</strong> {result.totalInvestment.toLocaleString()} 원</p>
                <p><strong>누적 배당금:</strong> {result.totalDividends.toLocaleString()} 원</p>
            </Summary>
        </ResultWrapper>
    );
};


// 스타일 컴포넌트 (원래 있던 것 쓰면 돼)
const ResultWrapper = styled.div`
    margin-top: 30px;
    padding: 20px;
    border-radius: 12px;
    background: #f9f9f9;
    //box-shadow: 0 2px 6px rgba(0,0,0,0.1);
`;

const Summary = styled.div`
    margin-top: 20px;
    font-size: 1.1rem;
    font-weight: bold;
`;


export default DividendResult;
