import { useEffect } from "react";
import Chart from "chart.js/auto";

const DividendResult = ({ data }) => {
    useEffect(() => {
        if (!data) return;

        const ctx = document.getElementById("dividendChart");
        if (ctx) {
            new Chart(ctx, {
                type: "line",
                data: {
                    labels: data.years,
                    datasets: [
                        {
                            label: "배당금 누적 합계 (물가 반영)",
                            data: data.inflationAdjusted,
                            borderColor: "blue",
                            fill: false,
                        },
                        {
                            label: "배당금 누적 합계 (미반영)",
                            data: data.nominal,
                            borderColor: "green",
                            fill: false,
                        },
                    ],
                },
            });
        }
    }, [data]);

    return (
        <div>
            <h2>계산 결과</h2>
            <canvas id="dividendChart"></canvas>
        </div>
    );
};

export default DividendResult;
