import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
} from "chart.js";

// Chart.js에 필요한 요소 등록
ChartJS.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend
);

const DividendCalculator = () => {
    const [initialInvestment, setInitialInvestment] = useState("");
    const [monthlyInvestment, setMonthlyInvestment] = useState("");
    const [dividendYield, setDividendYield] = useState("");
    const [dividendGrowthRate, setDividendGrowthRate] = useState("");
    const [inflationRate, setInflationRate] = useState("");
    const [years, setYears] = useState(30);
    const [isTaxable, setIsTaxable] = useState(false);
    const [taxRate, setTaxRate] = useState("");
    const [chartData, setChartData] = useState(null);

    const handleSubmit = async (event) => {
        event.preventDefault();

        const response = await fetch("http://localhost:8080/api/calculate", {
            method: "POST",
            headers: {
                "Content-Type": "application/json",
            },
            body: JSON.stringify({
                initialInvestment: parseFloat(initialInvestment),
                monthlyInvestment: parseFloat(monthlyInvestment),
                dividendYield: parseFloat(dividendYield),
                dividendGrowthRate: parseFloat(dividendGrowthRate),
                inflationRate: parseFloat(inflationRate),
                years: parseInt(years),
                taxable: isTaxable,
                taxRate: parseFloat(taxRate),
            }),
        });

        const data = await response.json();

        setChartData({
            labels: data.years,
            datasets: [
                {
                    label: "총 투자금",
                    data: data.totalInvestments,
                    borderColor: "blue",
                    fill: false,
                },
                {
                    label: "연간 배당금",
                    data: data.annualDividends,
                    borderColor: "green",
                    fill: false,
                },
            ],
        });
    };

    return (
        <div>
            <h1>배당 재투자 계산기</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="number"
                    placeholder="초기 투자금"
                    value={initialInvestment}
                    onChange={(e) => setInitialInvestment(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="월 적립 투자금"
                    value={monthlyInvestment}
                    onChange={(e) => setMonthlyInvestment(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="배당률 (%)"
                    value={dividendYield}
                    onChange={(e) => setDividendYield(e.target.value)}
                />
                <input
                    type="number"
                    placeholder="배당 성장률 (%)"
                    value={dividendGrowthRate}
                    onChange={(e) => setDividendGrowthRate(e.target.value)}
                />
                <button type="submit">계산하기</button>
            </form>

            {chartData && (
                <div>
                    <h2>배당 재투자 그래프</h2>
                    <Line data={chartData} />
                </div>
            )}
        </div>
    );
};

export default DividendCalculator;
